import './card.css';
import React from 'react';
import axios from 'axios';

const VideoItem = ({ video, onVideoSelect }) => {

  const userNm = localStorage.getItem('userNm');

  const selarr = [];

  axios.get('/api/like')
    .then(res => {
      res.data.map((dt) => {
        if (dt.user === userNm && dt.data.id.videoId === video.id.videoId && dt.isLike === true) {
          selarr.push(video.id.videoId);
        }
        // else console.log(2, dt.data.id.videoId, '===', video.id.videoId, '==', dt.user, '===', dt.isLike)
      })
    })

  setTimeout(() => {
    if (selarr.length !== 0) {
      document.querySelector('.like-btn-card').style.display = 'none';
      document.querySelector('#already-liked').style.display = 'block';
    } else {

      document.querySelector('.like-btn-card').style.display = 'block';
      document.querySelector('#already-liked').style.display = 'none';
    }
  }, 1000);

  return (

    <div className='like-cards-ytlist' onClick={() => onVideoSelect(video)} type="button" key={video.id.videoId}>
      <div className="card-con" data-toggle="modal" data-target="#exampleModal" >
        <center>
          <img className="liked-img" src={video.snippet.thumbnails.medium.url} />
        </center>
        <p className="liked-title" >{video.snippet.title}</p>
        <p className="liked-des">{video.snippet.description}</p>
      </div>
      {/* <div>
        <div className="like-btn-card">
          <i className="fa fa-heart-o fa-2x"></i>
        </div>
        <div id='already-liked' style={{ display: 'none' }} className="like-btn-card">
          <i className="fa fa-heart fa-2x" aria-hidden="true"></i>
        </div>
      </div> */}
      <div className="card-icon-test" onClick={(e) => {
