import React from 'react';
import axios from 'axios';

class Login extends React.Component {

    state = {
        email: '',
        password: '',
        loggedIn: ''
    }

    onInputChange = (e) => {
        this.setState({
            [e.target.name]: [e.target.value]
        })

    }

    onRegisterHandle = () => {
        const { email, password } = this.state;
        console.log(1212,email, password)
        axios.post('/api/user', { email, password })
            .then(res => {
                console.log(11, res.data)

                if(res.data === 11000){
                    alert('this user already exists.')
                }else alert('User registered successfully...');
                // ,
                // this.setState({
                //     loggedIn: true
                // })
            })
            .catch(err => console.log(11, err))
    }

    onLoginHandle = () => {
        const { email, password } = this.state;

        axios.get('/api/user')
            .then(res => {
                console.log(232,res.data)
                res.data.map( user => {
                    if(email === user.email && password === user.password){
                        this.setState({
                            loggedIn: user.email
                        })
                    }
                })
            })
            .catch( err => console.log(234, err))

            console.log(email,password)

    }

    render() {
        console.log(this.state.loggedIn)
        return (
            <div>
                <div class="modal fade" id="loginModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                    <div class="modal-dialog" role="document">
                        <div class="modal-content">
                            {/* <div class="modal-header">
                                <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
                                <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">&times;</span>
                                </button>
                            </div> */}
                            <div class="modal-body">
                                <form onSubmit={this.onSubmitHandle}>
                                    <div class="form-group row">
                                        <label for="staticEmail" class="col-sm-2 col-form-label">Email</label>
                                        <div class="col-sm-10">
                                            <input type="text" class="form-control" onChange={this.onInputChange} name='email' placeholder='Email' />
                                        </div>
                                    </div>
                                    <div class="form-group row">
                                        <label for="inputPassword" class="col-sm-2 col-form-label">Password</label>
                                        <div class="col-sm-10">
                                            <input type="password" class="form-control" onChange={this.onInputChange} name='password' placeholder="Password" />
                                        </div>
                                    </div>
                                </form>
                            </div>
                            <div class="modal-footer">
                                {/* <button type="button" class="btn btn-secondary" data-dismiss="modal">Register</button> */}
                                <button type="submit" onClick={this.onRegisterHandle} class="btn btn-secondary">Register</button>
                                <button type="submit" onClick={this.onLoginHandle} class="btn btn-primary">Login</button>

                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Login;
