    axios.get('/api/like')
        .then(res => {
            res.data.map((dt) => {
                // console.log(1,dt)
                if (dt.data.id.videoId === video.id.videoId) {
                    // // setCount('true')
                    if (dt.isLike === true) {
                        console.log(1, dt.data.id.videoId, '===', video.id.videoId)
                        document.querySelector('.like-btn').style.display = 'none';
                        document.querySelector('#already-liked').style.display = 'block';
                        //     // setCount('false')

                    } 
                    else {
                        //     console.log(3,count)
