import React, { useState } from 'react';
import axios from 'axios';

const VideoDetail = ({ video }) => {

    const [count, setCount] = useState('true');

    // var checked = false;
    if (!video) {
        return (
            <div >
            </div>
        );
    }

    const userNm = localStorage.getItem('userNm');

    const likeUnBtn = () => {
        //     axios.get('/api/like')
        //         .then(res => {
        //             res.data.map((dt) => {
        //                 // console.log(1,dt)
        //                 if (dt.data.id.videoId !== video.id.videoId) {
        //                     // // setCount('true')
        //                     document.querySelector('.like-btn').style.display = 'block';
        //                     document.querySelector('#already-liked').style.display = 'none';
        //                     // if (dt.isLike === true) {
        //                     //     console.log(1, dt.data.id.videoId, '===', video.id.videoId)
        //                     //     document.querySelector('.like-btn').style.display = 'none';
        //                     //     document.querySelector('#already-liked').style.display = 'block';
        //                     //     //     // setCount('false')

        //                     // } else {
        //                     //     //     console.log(3,count)
        //                     //     console.log(2, dt.data.id.videoId, '===', video.id.videoId)

        //                     //     document.querySelector('.like-btn').style.display = 'block';
        //                     //     document.querySelector('#already-liked').style.display = 'none';
        //                     // }
        //                 }
        //                 //  else {

        //                 //     document.querySelector('.like-btn').style.display = 'block';
        //                 //     document.querySelector('#already-liked').style.display = 'none'; 
        //                 // }
        //             })
        //         })

    }


    const selarr = [];

    axios.get('/api/like')
        .then(res => {
            res.data.map((dt) => {
                if (dt.user === userNm && dt.data.id.videoId === video.id.videoId && dt.isLike === true) {
                    // console.log(1, dt.data.id.videoId, '===', video.id.videoId, '==', dt.user, '===', dt.isLike)
                    selarr.push(video.id.videoId);
                } 
                // else console.log(2, dt.data.id.videoId, '===', video.id.videoId, '==', dt.user, '===', dt.isLike)
                // if (dt.user === userNm) {
