                //             document.querySelector('#already-liked').style.display = 'none';
                //         }
                //     }
                //     console.log(3, dt.data.id.videoId, '===', video.id.videoId, '==', dt.user, '===', dt.isLike)
                // }
                // console.log(4, dt.data.id.videoId, '===', video.id.videoId, '==', dt.user, '===', dt.isLike)
            })
        })

    console.log('selarr123', selarr.length)

    if (selarr.length === 0) {
        console.log('selarr12',selarr.length)
        // document.querySelector('.like-btn').style.display = 'none';
        // document.querySelector('#already-liked').style.display = 'block';
    }else{
        console.log('selarr13',selarr.length)
        // document.querySelector('.like-btn').style.display = 'block';
        // document.querySelector('#already-liked').style.display = 'none';
    }

    const likeBtn = () => {

        const user = userNm;
        const data = video;
        const isLike = true;
        axios.post('/api/like', { user, data, isLike })
            .then(res => {
                console.log(res.status)
                if (res.status === 200) {
                    // setCount(...count, res.data)
                    console.log(res.data)
                    document.querySelector('.like-btn').style.display = 'none';
                    document.querySelector('#already-liked').style.display = 'block';
                }
            })
            .catch(err => console.log(err))
        // setTimeout(() => {
        //     document.querySelector('.like-btn').style.display = 'block';
        // }, 30000);
    }

    const VidSrc = `https://www.youtube.com/embed/${video.id.videoId}`;



    return (
        <div className="modal fade" id="exampleModal" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog" role="document">
                <div className="modal-content" style={{ background: '#073946' }} >
                    <div className="modal-header">
                        <h5 className="modal-title, myHomefont" id="exampleModalLabel" style={{ color: 'rgb(243, 188, 15)' }} >{video.snippet.title}</h5>
                        <button type="button" onClick={likeUnBtn} className="close" data-dismiss="modal" aria-label="Close">
                            <span aria-hidden="true" title='close' style={{ color: 'white' }}>&times;</span>
                        </button>


                        {selarr.length === 0 ? (<div>
                        <div className="like-btn" onClick={likeBtn}>
                            <i className="fa fa-heart-o fa-2x"></i>
                        </div>
                        <div id='already-liked' style={{ display: 'none' }} className="like-btn">
                            {/* <span>Already Liked</span> */}
                            <i className="fa fa-heart fa-2x" aria-hidden="true"></i>
                        </div>
                        </div>):(<div>
                        <div className="like-btn" style={{ display: 'none' }} onClick={likeBtn}>
                            <i className="fa fa-heart-o fa-2x"></i>
                        </div>
                        <div id='already-liked' className="like-btn">
                            {/* <span>Already Liked</span> */}
                            <i className="fa fa-heart fa-2x" aria-hidden="true"></i>
                        </div>
                        </div>)}

                        {/* {
                            axios.get('/api/like')
                                .then(res => {
                                    res.data.map((dt) => {
                                        if (dt.data.id.videoId === video.id.videoId) {
                                        console.log(1212,video.id.videoId)
                                        } else {
                                            console.log(1212,'Not Found',video.id.videoId)
                                        }
                                    })
                                })

                        } */}

                    </div>
                    <div className="modal-body">
                        <iframe id="video-iframe" allowFullScreen="allowfullscreen" style={{ height: '300px', width: '100%' }} className="" title='video player' src={VidSrc} />

                    </div>

                </div>
            </div>
        </div>
    );
}

export default VideoDetail;
